import React from 'react';
import { StyleSheet, View, Image } from 'react-native';
import * as ImagePicker from 'expo-image-picker';

import { CustomText } from './CustomText';
import { IconBtn } from './IconBtn';
import { ICONS_LIGHT, COLORS } from '../styles';
import { Seperator } from './Seperator';
import { askForCameraPermissions } from '../utils/askForCameraPermissions';
import { imagePickerOptions } from '../utils/imagePickerOptions';
import fbApp from '../utils/FireBaseInit';

export const ProfilePictureLoader = () => {
	const selectImage = async (isCamera) => {
		try {
			let permisions = await askForCameraPermissions();
			if (permisions) {
				let image;
				if (isCamera) {
					image = await ImagePicker.launchCameraAsync(imagePickerOptions);
				} else {
					image = await ImagePicker.launchImageLibraryAsync(imagePickerOptions);
				}
				const { canceled, uri } = image;
				if (!canceled) {
				}
			}
		} catch (err) {
			console.log('selectImage err', err);
			//todo handle err
		}
	};
	return (
		<View style={styles.container}>
			<View style={styles.imageWrapper}>
				<Image style={styles.profilePic} source={{}} />
			</View>
			<CustomText weight="semi" style={styles.editText}>
				Edit Picture
			</CustomText>
			<Seperator color={COLORS.acsentColor} style={styles.seperator} />
			<View style={styles.row}>
				<IconBtn icon={ICONS_LIGHT.camera} style={styles.icon} onPress={() => selectImage(true)} />
				<IconBtn icon={ICONS_LIGHT.gallery} style={styles.icon} onPress={() => selectImage()} />
			</View>
		</View>
	);
};
async function uploadImage(uri) {
	try {
		fbApp
	} catch (err) {
		console.log(err);
	}
}

const styles = StyleSheet.create({
	container: {
		justifyContent: 'center',
		alignItems: 'center',
		marginTop: 50
	},
	imageWrapper: {
		width: 200,
		height: 200,
		borderRadius: 100,
		borderWidth: 20,
		borderColor: COLORS.acsentColor,
		marginBottom: 17
	},
	chooseBtn: {
		alignSelf: 'flex-end',
		marginRight: 68
	},
	seperator: {
		marginTop: 10,
		width: '80%'
	},
	row: {
		width: 80,
		flexDirection: 'row',
		justifyContent: 'space-between',
		alignItems: 'center',
		marginTop: 10
	},
	editText: {
		color: COLORS.acsentColor,
		fontSize: 12,
		alignSelf: 'center'
	}
});
