export const GLOBAL_STYLES = Object.freeze({
   //global styles for buttom shaddows everywhere
	shaddowBottum: {
		paddingHorizontal: 25,
		shadowColor: '#000',
		shadowOffset: {
			width: 0,
			height: 1
		},
		shadowOpacity: 0.18,
		shadowRadius: 1.0,

		elevation: 1
   },
   
   //global styles for top shaddows everywhere like footer
	shaddowTop: {
		shadowColor: '#000',
		shadowOffset: {
			width: 0,
			height: 3
		},
		shadowOpacity: 0.29,
		shadowRadius: 4.65,

		elevation: 7
	}
});
